import { Box, Grid, TextField, Typography } from '@material-ui/core';
import React from 'react';

function Result(props) {
	const currentCalc = props.currentCalculation;
	const history = props.history.map((h, i) => (
		<Grid item container>
			<Grid item container xs="8">
				<Typography className="helvetica-neue-font ">{h.answer}</Typography>
			</Grid>
			<Grid item container xs="4" justify="flex-end">
				<Typography className="helvetica-neue-font ">{h.equation}</Typography>
			</Grid>
		</Grid>
	));
	return (
		<Grid item container direction="column">
			{history}
			<Grid item container>
				<Grid item container xs="8"></Grid>
				<Grid
					item
					container
					xs="4"
					justify="flex-end"
					className="current-result"
				>
					<Typography className="helvetica-neue-font ">
						{!isNaN(currentCalc) && currentCalc}
					</Typography>
				</Grid>
			</Grid>
		</Grid>
	);
}

export default Result;
