import { Grid } from '@material-ui/core';
import React, { Component } from 'react';
import Calc from './Calc-comps/Calc';
import Screen from './Calc-comps/Screen';

export default class componentName extends Component {
	state = { currentCalculation: 0 };
	calculate = (equation) => {
		//4-5•6÷9•9+4•6÷5•9+563•7÷6÷6

		let calc = equation
			.split(/(•|÷|\+|-)/)
			.filter((c, i) => c !== '')
			.flatMap((c, i, arr) => {
				if (c === '-') {
					if (arr[i + 1] === '+') {
						arr[i + 1] = '-';
						return [];
					} else if (arr[i + 1] === '-') {
						arr[i + 1] = '+';
						return [];
					}
				} else if (c === '+') {
					if (arr[i + 1] === '-') {
						arr[i + 1] = '-';
						return [];
					} else if (arr[i + 1] === '+') {
						arr[i + 1] = '+';
						return [];
					}
				}
				return parseInt(c) || c;
			})
			.reverse();
		console.log(calc, 'oripre');
		for (let i = calc.length - 1; i >= 0; i--) {
			if (calc[i + 1] && calc[i - 1]) {
				if (calc[i] === '•') {
					calc[i - 1] = calc[i + 1] * calc[i - 1];
					calc.splice(i + 1, 1);
					calc.splice(i, 1);
				} else if (calc[i] === '÷') {
					calc[i - 1] = calc[i + 1] / calc[i - 1];
					calc.splice(i + 1, 1);
					calc.splice(i, 1);
				}
			}
		}
		calc = calc.reverse();
		console.log(calc, 'ori');

		const calc2 = calc.reduce((a, c, i, arr) => {
			if (typeof arr[i - 1] === 'string') return a;
			if (c === '-') {
				return a - arr[i + 1];
			} else if (c === '+') {
				return arr[i + 1] + a;
			} else {
				return a + c;
			}
		}, 0);
		console.log(calc, calc2);
		this.setState({ currentCalculation: calc2 });
	};

	render() {
		return (
			<Grid
				sm="10"
				md="8"
				lg="6"
				xl="4"
				direction="column"
				justify="center"
				alignItems="flex-start"
				item
				className="main-cont"
				container
			>
				<Screen
					calculate={this.calculate}
					currentCalculation={this.state.currentCalculation}
				/>
				<Calc />
			</Grid>
		);
	}
}
