import { Grid, InputAdornment, TextField } from '@material-ui/core';
import React, { useState } from 'react';
import reactDom from 'react-dom';
import Result from './Result';

function Screen(props) {
	const setInputFocus = () => {
		reactDom.findDOMNode(ref).focus();
	};
	const setRe = (x) => {
		props.setRe(x);
	};
	return (
		<Grid
			item
			container
			className="screen-cont"
			justify="center"
			alignContent="flex-end"
			// style={{ background: 'orange' }}
		>
			<Grid
				item
				container
				xs={10}
				className="history-cont"
				// style={{ background: 'green' }}
			>
				<Result
					currentCalculation={props.currentCalculation}
					history={props.history}
				/>
			</Grid>
			<Grid item xs={10} className="current-result-cont">
				{/* <Result
					currentCalculation={props.currentCalculation}
					history={props.history}
				/> */}
				<form
					noValidate
					onSubmit={(e) => {
						e.preventDefault();
						if (props.term !== '') {
							props.submit();
						}
					}}
					className="form-screen"
				>
					<TextField
						inputRef={(t) => {
							if (t !== null) props.focus(t);
						}}
						id="outlined-basic"
						color="secondary"
						autoFocus={true}
						autoComplete="off"
						required={true}
						helperText=""
						variant="outlined"
						fullWidth={true}
						value={props.term}
						onChange={(e) => {
							props.changeInput(e.target.value);
						}}
						InputProps={{
							endAdornment: (
								<InputAdornment position="start" disablePointerEvents={true}>
									{!isNaN(props.currentCalculation) && props.currentCalculation}
								</InputAdornment>
							),
						}}
					></TextField>
				</form>
			</Grid>
			<button onClick={setInputFocus}>xd</button>
		</Grid>
	);
}

export default Screen;
