import { Grid } from '@material-ui/core';
import React, { Component } from 'react';
import Calc from './Calc-comps/Calc';
import Screen from './Calc-comps/Screen';

export default class componentName extends Component {
	calculate = (equation) => {
		const calc = equation.split('').reduce((a, c, i, arr) => {
			let converted;
			if (/^([0-9])$/.test(c)) {
				converted = parseFloat(c);
				return converted;
			}
		});
		console.log(equation);
	};

	render() {
		return (
			<Grid
				sm="10"
				md="8"
				lg="6"
				xl="4"
				direction="column"
				justify="center"
				alignItems="flex-start"
				item
				className="main-cont"
				container
			>
				<Screen calculate={this.calculate} />
				<Calc />
			</Grid>
		);
	}
}
